sections.css:
.header {
    position: relative;

    min-width: $mainContMinWidth;
    margin-bottom: 0;
    padding: 0 0 $vR * 5;

    background: darken($liver, 12);
    background-size: cover;

    &__bg {
        position: absolute;
        z-index: 0;
        top: 0;
        left: 0;

        width: 100%;
        height: 100%;

        opacity: .07;
        background: $liver no-repeat 50% 50%;
        background-size: cover;
    }

    &__content {
        position: relative;
        z-index: 2;
    }

    &__logo {
        display: block;

        width: $vR * 14;
        margin: 0 auto $vR * 7;

        text-align: center;
        text-decoration: none;

        font-family: $fontTitle;
        font-size: 21px;
        font-weight: 300;

        &, &:visited {
            color: rgba(#fff, .5);
        }

        &:hover, &:hover:visited {
            color: $golden;
        }

        &-img {
            width: $vR * 9;
            margin-bottom: $vR;

            border-radius: 50%;
        }

        &-text {
            letter-spacing: 1px;
        }
    }

    &__intro {
        text-align: center;

        color: rgba(#fff, .5);

        font-family: $fontTitle;
        font-size: $fontBase * .8;

        &-main {
            padding-bottom: 6px;

            color: rgba(#fff, .7);

            font-size: $fontBase * 1.5;
            font-weight: 500;
        }

        &-sep {
            position: relative;

            width: 100px;
            margin: $vR * 4 + 2 auto $vR * 4;

            border-top: 2px solid rgba(#fff, .2);

            &:before {
                position: absolute;
                top: 50%;
                left: 50%;

                display: block;

                width: 4px;
                height: 13px;
                margin-top: -7px;
                margin-left: -2px;

                content: '';
                transform: rotate(45deg);

                border-right: 1px solid rgba(#fff, .3);
                border-left: 1px solid rgba(#fff, .3);
            }
        }
    }

    &__place {
        margin-top: $vR;
    }

    &__contacts {
        margin: $vR * 3 0 0 0;
        padding: 0;

        list-style: none;

        font-size: $fontBase;

        li {
            display: inline-block;

            a {
                text-decoration: none;

                color: rgba(#fff, .5);

                font-size: $fontBase * 1.2;

                &:hover {
                    color: $golden;
                }

                i {
                    position: relative;
                    top: 2px;
                }
            }
        }
    }

    &__top {
        position: relative;
        z-index: 2;

        padding: $vR * 2 $vR * 2 $vR * 3;

        .header__logo {
            position: relative;

            display: inline-block;

            width: $vR * 5;
            height: $vR * 5;
            margin-right: 27px;
            margin-bottom: 0;

            &:after {
                position: absolute;
                top: 0;
                right: -18px;

                height: $vR * 5;

                content: '';

                border-right: 1px solid rgba(#fff, .3);
            }

            img {
                width: $vR * 5;
            }
        }

        .header__contacts {
            position: relative;
            top: -3px;

            display: inline-block;

            margin: 0;

            font-size: $fontSmall;
        }
    }

    &__tag-title {
        text-transform: uppercase;
    }

    .nav {
        top: 1px;

        float: right;
    }
}


.header.-post {
    position: relative;

    padding: 0;

    .header__content {
        position: relative;

        max-width: $mainContMaxWidth;
        margin: 0 auto;
        padding: $vR * 10 21px $vR * 14 + 6;

        text-align: center;

        color: rgba(#fff, .8);
        text-shadow: 0 0 1px rgba($colorText, .1);

        .title {
            margin-top: 0;
            margin-bottom: $vR * 5 - 3;

            word-break: break-word;
        }

        &-desc {
            padding-bottom: $vR;
        }
    }

    .header__post-info {
        padding-top: $vR - 2;

        text-transform: uppercase;

        color: rgba(#fff, .5);

        font-family: $fontTitle;
        font-size: $fontTiny;
        font-weight: normal;

        &-date {
            margin-right: 16px;

            &:after {
                position: absolute;

                display: inline-block;

                box-sizing: border-box;
                width: 16px;
                margin-right: 0;
                padding-top: 0;
                padding-right: 0;

                content: '•';
                text-align: right;

                font-size: $fontBase * 1.3;
            }
        }

        &-tags {
            position: relative;

            display: inline-block;

            margin: 0;
            padding: 0;

            list-style: none;

            li {
                display: inline-block;

                margin: 0 4px;

                a, a:visited {
                    color: rgba(#fff, .5);

                    &:hover {
                        color: $colorLinkHover;
                    }
                }
            }
        }
    }
}

@media screen and (max-width: 479px) {
    .header.-post {

        &__top {

            .header__contacts {
                top: 3px;

                float: right;
            }

            .nav {
                display: block;
                float: none;

                margin-top: $vR * 3;

                text-align: center;
            }
        }

        &__content {
            padding-top: $vR * 6;
            padding-bottom: $vR * 9 + 6;
        }
    }
}

@media screen and (max-width: 520px) {
    .header.-post {

        .header__content {

            .title {
                margin-bottom: $vR * 5;

                font-size: $fontBase * 1.7;
            }

            &-desc {
                padding-bottom: $vR;
            }
        }
    }
}

@font-face {
  font-family: 'fontello';
  src: url('/font/fontello.eot?66738560');
  src: url('/font/fontello.eot?66738560#iefix') format('embedded-opentype'),
       url('/font/fontello.woff?66738560') format('woff'),
       url('/font/fontello.ttf?66738560') format('truetype'),
       url('/font/fontello.svg?66738560#fontello') format('svg');
  font-weight: normal;
  font-style: normal;
}

[class^="icon-"]:before, [class*=" icon-"]:before {
  font-family: "fontello";
  font-style: normal;
  font-weight: normal;
  speak: none;

  display: inline-block;
  text-decoration: inherit;
  width: 1em;
  margin-right: .2em;
  text-align: center;

  font-variant: normal;
  text-transform: none;

  line-height: 1em;

  margin-left: .2em;
}

.icon-ios-location:before { content: '\e800'; } /* '' */
.icon-social-github:before { content: '\e801'; } /* '' */
.icon-social-twitter-outline:before { content: '\e802'; } /* '' */
.icon-ios-email-outline:before { content: '\e803'; } /* '' */
.icon-ios-search-strong:before { content: '\e804'; } /* '' */
.icon-social-linkedin-outline:before { content: '\e805'; } /* '' */
.icon-close:before { content: '\e806'; } /* '' */

.highlight {
    line-height: 24px;

    margin: $vR * 5 auto $vR * 5 - 1 auto;
    padding: $vR * 2 - 2px $vR * 2 $vR + 2;

    figcaption {
        font-size: $fontCode;

        margin-bottom: $vR * 2;
        padding: 0 0 $vR + 4 0;

        opacity: .8;
        border-bottom: 1px solid rgba(0, 0, 0, .1);
    }
}

.highlight pre {
    font-size: $fontCode;

    margin: 0;

    white-space: pre-wrap;
    word-wrap: break-word;
}

@media screen and (max-width: 520px) {
    .highlight {
        margin: ($vR * 4) auto ($vR * 4 - 1) auto;
    }
}

.img {
    position: relative;

    margin: $blockSepSpace auto $blockSepSpace auto;

    text-align: center;

    img {
        max-width: 100%;
    }

    &__img-cont {
        padding-right: 300px;
    }

    &__desc {
        position: absolute;
        top: $vR * 2;
        right: 0;

        width: 300 - 18px;

        text-align: left;

        color: $colorLightText;

        font-family: $fontTitle;
        font-size: $fontSmall;
        line-height: $vR * 3;

        &:before {
            position: absolute;
            top: -($vR * 2);
            left: 0;

            width: 80px;

            content: "";

            border-top: 1px solid rgba(0, 0, 0, .15);
        }
    }
}

@media screen and (max-width: 940px) {
    .img {

        &__img-cont {
            padding-right: 0;
        }

        &__desc {
            position: relative;
            top: 0;
            right: 0;

            width: 100%;
            margin: $vR auto 0;

            &:before {
                display: none;
            }
        }
    }
}

.quote, .post blockquote {
    margin: $vR * 8 auto $vR * 8 auto;

    text-align: center;

    color: $colorLightText;

    font-size: $fontBase * 1.2;
    font-style: italic;

    &.-before-header {
        margin-bottom: $vR * 5;
    }
}

.share {
    padding: $vR 0 $vR * 3;

    text-transform: uppercase;

    font-family: $fontTitle;
    font-size: $fontTiny;

    &__item {
        text-decoration: none;

        .icon {
            position: relative;
            top: 1px;

            font-size: $fontMiddle;
        }
    }
}

.post {
    letter-spacing: 0.159999996423721px;

    -webkit-font-smoothing: antialiased;
    text-rendering: optimizeLegibility;

    word-break: break-word;

    padding-top: $vR * 10;

    :first-child {
        margin-top: 0;
    }

    &.-no-padding-top {
        padding-top: 0;
    }

    p, ul, ol, h1, h2, h3, h4, h5, h6 {
        max-width: $narrowContMaxWidth;
        margin-right: auto;
        margin-left: auto;
    }

    p {
        &.-notice {
            position: relative;
            left: -27px;

            display: block;

            padding: 0 0 0 24px;

            border-left: 3px solid rgba($fuscousGrey, .4);
        }
    }

    ul, ol {
        margin-bottom: ($vR * 4) - 1;
        padding: 0;

        list-style: none;
        list-style-image: none;

        counter-reset: post;

        li {
            margin-bottom: 12px;
            margin-left: 30px;

            font-size: $fontBase;
        }
    }

    ul li:before {
        position: absolute;

        display: inline-block;

        box-sizing: border-box;
        width: 58px;
        margin-left: -58px;
        padding-right: 12px;

        content: '•';
        text-align: right;

        font-size: $fontBase;
    }

    ol li:before {
        position: absolute;

        display: inline-block;

        box-sizing: border-box;
        width: 58px;
        margin-left: -58px;
        padding-right: 12px;

        content: counter(post) ".";
        counter-increment: post;
        text-align: right;
    }

    h3 + ul, h3 + ol {
        margin-top: $vR * 3;
    }

    h3 + h4, h3 + h4 {
        margin-top: $vR;
    }

    hr {
        display: block;

        width: 144px;
        margin: $blockSepSpace - 1 auto $blockSepSpace - 2 auto;

        border: 0;
        border-top: 1px solid rgba(0, 0, 0, .15);
    }

    &-contacts {
        font-family: $fontTitle;
        font-weight: 200;
        font-size: $fontMiddle;
    }
}

.posts-list {
    text-align: center;

    padding-top: $vR * 8;

    &__item {
        position: relative;

        &:before {
            display: block;

            width: 144px;
            margin: $blockSepSpace auto $vR * 7 auto;

            content: '';

            border: 0;
            border-top: 1px solid rgba(0, 0, 0, .15);
        }

        &:first-child {
            &:before {
                display: none;
            }
        }

        &-title {
            font-weight: 400;
            word-break: break-word;
        }

        &-info {
            margin-bottom: $vR * 2;

            text-transform: uppercase;

            color: $colorSubTitle;

            font-family: $fontTitle;
            font-size: $fontTiny;
            font-weight: normal;
            line-height: $vR * 5;

            &-date {
                margin-right: 16px;

                &:after {
                    position: absolute;

                    display: inline-block;

                    box-sizing: border-box;
                    width: 16px;
                    margin-right: 0;
                    padding-top: 0;
                    padding-right: 0;

                    content: '•';
                    text-align: right;

                    font-size: $fontBase * 1.3;
                }
            }

            &-tags {
                position: relative;

                display: inline-block;

                margin: 0;
                padding: 0;

                list-style: none;

                li {
                    display: inline-block;

                    margin: 0 4px;

                    a, a:visited {
                        color: $colorSubTitle;

                        &:hover {
                            color: $colorLinkHover;
                        }
                    }
                }
            }
        }

        &-desc {
            font-size: $fontMiddle;
            line-height: $vR * 3;
        }
    }

    .quote {
        margin: 0;
    }
}

.footer {
    margin-top: $vR * 7;

    color: rgba(#fff, .4);
    background: $colorFooterBg;

    font-family: $fontTitle;

    word-break: break-word;

    &__nav {
        padding: $vR * 4 0 $vR * 2;

        text-align: center;
    }

    &__copyright {
        padding: 0 0 $vR * 4 0;

        text-align: center;

        font-size: $fontTiny;
        font-weight: normal;
    }

    &__post-switcher {
        display: table;

        width: 100%;
        min-width: 160px;

        border-spacing: 0;

        border: 0;
        border-collapse: collapse;
        background: #fff;

        &-link {
            display: table-cell;

            width: 50%;
            padding: $vR * 6;

            transition: transform .1s;
            text-decoration: none;

            border-collapse: collapse;
            background: $colorFooterBg;

            span {
                display: block;
            }

            &:hover {
                transform: translateY(-$vR);
            }

            &.-prev {
                border-right: 1px solid lighten($colorFooterBg, 10);
            }

            &.-empty {
                &:hover {
                    transform: translateY(0);
                }
            }
        }

        &-direction {
            text-transform: uppercase;

            color: rgba(#fff, .5);

            font-size: $fontTiny;
        }

        &-title {
            margin-top: $vR * 3;

            font-size: $fontBase * 1.2;
        }
    }

    &.-main {
        .footer__post-switcher-direction {
            font-size: $fontMiddle;
        }
    }
}

@media screen and (max-width: 750px) {
    .footer {

        &__post-switcher {

            &-link {
                padding: $vR * 3;
            }
        }
    }
}

@media screen and (max-width: 480px) {
    .footer {

        &__post-switcher {

            &-link {
                padding: $vR * 2;
            }
        }
    }
}

.section {
    letter-spacing: 0.159999996423721px;

    -webkit-font-smoothing: antialiased;
    text-rendering: optimizeLegibility;

    &.-narrow {
        max-width: $narrowContMaxWidth;
        margin: 0 auto;
    }

    &.-bottom
    {
        margin-top: $blockSepSpace;

        border-top: 1px solid rgba(0, 0, 0, .15);
    }
}

.section-divider {
    display: block;

    width: 144px;
    margin: $blockSepSpace - 1 auto $blockSepSpace - 2 auto;

    border: 0;
    border-top: 1px solid rgba(0, 0, 0, .15);
}

.search {
    position: fixed;
    top: 0;
    right: 0;

    overflow: hidden;

    width: 0;
    height: 0;

    transition: all .3s;
    text-align: center;

    font-family: $fontTitle;

    &.-active {
        z-index: 10;

        width: 100%;
        height: 100%;

        background: rgba(mix($colorLightText, #fff, 20%), .97);
    }

    &__container {
        position: relative;
        top: 50%;

        transform: translateY(-50%);

        color: $colorText;

        font-size: $fontBase * 2;
    }

    &__input {
        width: 70%;

        border: 0;
        border-bottom: 1px solid rgba($fuscousGrey, .15);
        background: transparent;

        &:focus {
            outline: 0;
        }
    }

    button {
        border: 0;
        background: transparent;

        &:focus {
            outline: 0;
        }
    }

    &__icon {
        color: rgba($fuscousGrey, .15);
    }

    &__close {
        position: absolute;
        top: $vR * 2;
        right: $vR * 2;

        color: rgba($fuscousGrey, .5);

        i {
            transition: transform .5s;
        }

        &:hover, &:visited:hover {
            color: rgba($fuscousGrey, .5);

            i {
                transform: rotate(360deg);
            }
        }
    }
}
